// Tipo para un paciente
export type Paciente = {
    id: number;
    nombre: string;
    identificacion: string;
  };
  
// Tipo para un signo vital
export type SignoVital = {
  id: number;
  descripcion: string;
  nivelMinimo: number;
  nivelMaximo: number;
};
  
// Tipo para un control realizado
export type ControlRealizado = {
  id: number;
  idPaciente: number;
  idSignoVital: number;
  fecha: string; // Considerar usar el tipo Date si se prefiere
  hora: string;
  valor: number;
};
